"""
Leetcode 76: Minimum Window Substring
Given two strings s and t of lengths m and n respectively, return the minimum window substring of s such that every
character in t (including duplicates) is included in the window. If there is no such substring, return the empty
string "".

My first approach evaluated every possible substring to find the minimal match which performed O(m^2) worst case.
That led to the current approach of maintaining a window with two indexes and a running count of desired characters.
The window expands to the right when until it finds a match and shrinks on the left when a match has been found, which
provides a worst case of O(m + n).
"""


def window_match(window_count: dict) -> bool:
    """
    Return True if count of characters in window indicates it is a match
    :param window_count: dictionary char -> count
    :return: True if there is a match, False otherwise
    """
    for key in window_count:
        if window_count[key] > 0:
            return False
    return True


class Solution:

    def minWindow(self, s: str, t: str) -> str:
        """
        Find minimum window of characters in s that have all characters in t
        :param s: Window string
        :param t: Target string
        :return: Minimal window
        """
        window_count = {key: t.count(key) for key in t}
        min_substring = ""
        right = 0
        left = 0
        while right < len(s):
            if s[right] in window_count:
                window_count[s[right]] -= 1
                if window_match(window_count):
                    if not min_substring:
                        min_substring = s[left:right + 1]
                    elif right - left < len(min_substring):
                        min_substring = s[left:right + 1]
                    while left < right:
                        if s[left] in window_count:
                            window_count[s[left]] += 1
                        left += 1
                        if window_match(window_count):
                            if right - left < len(min_substring):
                                min_substring = s[left:right + 1]
                        else:
                            break
            right += 1
        return min_substring


"""
Test cases
"""


def test_empty():
    sol = Solution()
    assert sol.minWindow("", "a") == ""


def test_single_char():
    sol = Solution()
    assert sol.minWindow("a", "a") == "a"


def test_two_chars():
    sol = Solution()
    assert sol.minWindow("a", "aa") == ""


def test_longer():
    sol = Solution()
    assert sol.minWindow("ADOBECODEBANC", "ABC") == "BANC"


def test_ab():
    sol = Solution()
    assert sol.minWindow("ab", "a") == "a"


def test_longer():
    sol = Solution()
    assert sol.minWindow("aaaaaaaaaaaabbbbbcdd", "abcdd")


def test_big():
    s = ""
    t = ""
    sol = Solution()
    sol.minWindow(s, t)


def test_welsh():
    s = "cabwefgewcwaefgcf"
    t = "cae"
    sol = Solution()
    assert sol.minWindow(s, t) == "cwae"
